sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	var ajax = new GlideAjax('x_navyn_badges.ClientDateTimeUtils');
	ajax.addParam('sysparm_name', 'getNowDate');
	ajax.getXML(doSomething);



	function doSomething(response){

		var answer = response.responseXML.documentElement.getAttribute("answer");

		var status = g_form.getValue('u_status'); //Choose the field to add time from
		if (status=='5b86c5c8db092b00cf04fd0e0f9619a6')
			g_form.setValue('badge_issue_date',answer);
		if (status=='7520264cdbc92b00cf04fd0e0f9619d8')
			g_form.setValue('actual_badge_return_date', answer);



	}


}
